var link=document.querySelector(".address-button"),popup=document.querySelector(".modal-write-us"),close=popup.querySelector(".modal-close"),form=popup.querySelector("form"),login=popup.querySelector("[name=name]"),email=popup.querySelector("[type=email]"),text=popup.querySelector("textarea"),isStorageSupport=!0,storage="";ymaps.ready(init);try{storage=localStorage.getItem("login")}catch(e){isStorageSupport=!1}function init(){myMap=new ymaps.Map("map",{center:[59.9391,30.3214],zoom:17,controls:[]});var e=new ymaps.Placemark([59.938709,30.323081],{},{iconLayout:"default#image",iconImageHref:"img/map-marker.png",iconImageSize:[231,190],iconImageOffset:[-50,-190]});myMap.geoObjects.add(e),myMap.behaviors.disable(["drag","leftMouseButtonMagnifier","rightMouseButtonMagnifier","scrollZoom","dblClickZoom"])}link.addEventListener("click",function(e){e.preventDefault(),popup.classList.add("modal-show"),login.focus()}),close.addEventListener("click",function(e){e.preventDefault(),popup.classList.remove("modal-show")}),form.addEventListener("submit",function(e){login.value&&email.value&&text.value?isStorageSupport&&localStorage.setItem("login",login.value):(e.preventDefault(),console.log("Нужно ввести логин и пароль"))}),window.addEventListener("keydown",function(e){27===e.keyCode&&(e.preventDefault(),popup.classList.contains("modal-show")&&popup.classList.remove("modal-show"))});